graphql.query:
query everything {
    webApiServices {
        goid name resolutionPath checksum 
        enabled folderPath methodsAllowed tracingEnabled wssProcessingEnabled 
        properties { name value }
        policy { xml }
    }
    soapServices {
        goid name resolutionPath resolvers { soapActions baseUri resolutionPath } checksum 
        enabled folderPath soapVersion methodsAllowed tracingEnabled wssProcessingEnabled laxResolution 
        properties { name value }
        wsdl 
        policy { xml }
    }
    internalWebApiServices {
        goid name resolutionPath checksum 
        enabled folderPath methodsAllowed tracingEnabled wssProcessingEnabled 
        properties { name value } 
        policy { xml }
    }
    internalSoapServices {
        goid name resolutionPath resolvers { soapActions baseUri resolutionPath } checksum 
        enabled folderPath soapVersion methodsAllowed tracingEnabled wssProcessingEnabled laxResolution 
        properties { name value } 
        wsdl 
        policy { xml }
    }
    encassConfigs {
        goid guid name checksum 
        description policyName 
        encassArgs { name type ordinal guiPrompt guiLabel } 
        encassResults{ name type } 
        properties { name value }
    }
    globalPolicies {
        goid guid name tag checksum 
        folderPath 
        policy { xml }
    }
    backgroundTaskPolicies {
        goid guid name checksum 
        folderPath soap 
        policy { xml }
    }
    policyFragments {
        goid guid name checksum 
        folderPath soap 
        policy { xml }
    }

    activeConnectors {
        goid name checksum 
        enabled connectorType hardwiredServiceName 
        properties { name value } 
        advancedProperties { name value }
    }
    dtds {
        goid systemId checksum 
        publicId description content
    }
    cassandraConnections {
        goid name checksum 
        enabled keyspace contactPoints port compression username securePasswordName sslEnabled cipherSuites 
        properties { name value }
    }
    clusterProperties {
        goid name checksum 
        description hiddenProperty value
    }
    emailListeners {
        goid name checksum 
        enabled serverType hostname port folder deleteOnReceive username password hardwiredServiceName sslEnabled pollInterval sizeLimit 
        properties { name value }
    }
    fips {
        goid name checksum 
        enableCredentialTypeSaml enableCredentialTypeX509 certificateValidation 
        certificateReferences { 
             goid name subjectDn thumbprintSha1 checksum 
             verifyHostname trustAnchor trustedFor revocationCheckPolicyType 
             revocationCheckPolicy { goid name } 
             notBefore notAfter certBase64 
        }
    }
    fipGroups {
        goid name providerName checksum 
        description 
        members { 
            goid name login providerName checksum 
            subjectDn certBase64 firstName lastName email 
        }
    }
    fipUsers {
        goid name login providerName checksum 
        subjectDn certBase64 firstName lastName email 
        memberOf { 
            goid name providerName checksum 
            description 
        }
    }
    internalDtds {
        goid systemId checksum 
        publicId description content
    }
    internalGroups {
        goid name checksum 
        description 
        members { 
            goid name login checksum 
            enabled password certBase64 firstName lastName email 
        }
    }
    internalSchemas {
        goid systemId checksum 
        targetNs description content
    }
    internalUsers {
         goid name login checksum 
         enabled password certBase64 firstName lastName email 
         memberOf { goid name checksum description }
    }
    jdbcConnections {
         goid name checksum 
         enabled driverClass jdbcUrl username password minPoolSize maxPoolSize 
         properties { name value }
    }
    jmsDestinations {
         goid connectionGoid name direction providerType checksum 
         enabled template initialContextFactoryClassname connectionFactoryName jndiUrl jndiUsername jndiPassword 
         jndiSslDetails { sslEnabled sslForAuthenticationOnly sslVerifyServerCertificate sslVerifyServerHostname sslClientKeyAlias } 
         destinationType destinationName destinationUsername destinationPassword 
         destinationSslDetails { sslEnabled sslForAuthenticationOnly sslVerifyServerCertificate sslVerifyServerHostname sslClientKeyAlias } 
         properties { name value }
    }
    keys {
         goid keystoreId alias checksum 
         keyType subjectDn p12 certChain
    }
    ldaps {
        goid name checksum 
        ldapUrls ldapsClientAuthEnabled ldapsClientKeystoreId ldapsClientKeyAlias searchBase writable bindDn bindPassword 
        userMappings { objClass nameAttrName loginAttrName passwdAttrName firstNameAttrName lastNameAttrName emailNameAttrName kerberosAttrName kerberosEnterpriseAttrName userCertAttrName passwdType { val } } 
        groupMappings { objClass nameAttrName memberAttrName memberStrategy { val } }
    }
    listenPorts {
         goid name checksum 
         enabled protocol port hardwiredServiceName enabledFeatures 
         tlsSettings { clientAuthentication keystoreId keyAlias tlsVersions cipherSuites useCipherSuitesOrder } 
         properties { name value }
    }
    scheduledTasks {
         goid name checksum 
         policyName jobType cronExpression executeOnSingleNode executeOnCreation executionDate status runAsUser runAsUserProviderName
    }
    schemas {
         goid systemId checksum 
         targetNs description content
    }
    secrets {
         goid name checksum 
         description secret secretType variableReferencable
    }
    serverModuleFiles {
        goid name checksum 
        moduleType moduleSha256 signature signerCertBase64 
        properties { name value } 
        moduleStates { nodeId nodeName state description } 
        moduleStateSummary { state description }
    }
    smConfigs {
        goid name checksum 
        enabled agentHost agentIP agentHostConfig agentSecret cryptoMode ipCheckEnabled updateSSOToken clusterFailoverThreshold nonClusterFailover username securePasswordName 
        properties { name value }
    }
    trustedCerts {
         goid name subjectDn thumbprintSha1 checksum 
         verifyHostname trustAnchor trustedFor revocationCheckPolicyType 
         revocationCheckPolicy { goid name } 
         notBefore notAfter certBase64
    }
}

mutation applyBundle (
    $clusterProperties: [ClusterPropertyInput!]!=[],
    $webApiServices: [WebApiServiceInput!]!=[],
    $encassConfigs:[EncassConfigInput!]!=[],
    $trustedCerts:[TrustedCertInput!]!=[],
    $dtds:[DtdInput!]!=[],
    $schemas:[SchemaInput!]!=[],
    $jdbcConnections:[JdbcConnectionInput!]!=[],
    $soapServices:[SoapServiceInput!]!=[],
    $policyFragments: [PolicyFragmentInput!]!=[],
    $fips:[FipInput!]!=[],
    $ldaps:[LdapInput!]!=[],
    $fipGroups:[FipGroupInput!]!=[],
    $internalGroups:[InternalGroupInput!]!=[],
    $fipUsers:[FipUserInput!]!=[],
    $internalUsers:[InternalUserInput!]!=[],
    $keys: [KeyInput!]!=[],
    $secrets: [SecretInput!]!=[],
    $cassandraConnections:[CassandraConnectionInput!]!=[],
    $jmsDestinations:[JmsDestinationInput!]!=[],
    $internalWebApiServices: [WebApiServiceInput!]!=[],
    $internalSoapServices:[SoapServiceInput!]!=[],
    $emailListeners: [EmailListenerInput!]!=[],
    $listenPorts: [ListenPortInput!]!=[],
    $activeConnectors: [ActiveConnectorInput!]!=[],
    $smConfigs: [SMConfigInput!]!=[],
    $globalPolicies:[GlobalPolicyInput!]!=[],
    $backgroundTaskPolicies:[BackgroundTaskPolicyInput!]!=[],
    $scheduledTasks:[ScheduledTaskInput!]!=[],
    $serverModuleFiles:[ServerModuleFileInput!]!=[]) {
        setServerModuleFiles(input: $serverModuleFiles){serverModuleFiles{goid} detailedStatus {status description}}
        setClusterProperties (input: $clusterProperties){clusterProperties {goid} detailedStatus {status description}}
        setTrustedCerts (input: $trustedCerts){trustedCerts {goid} detailedStatus {status description}}
        setSecrets (input: $secrets){secrets{name} detailedStatus {status description}}
        setSchemas (input: $schemas){schemas {goid} detailedStatus {status description}}
        setDtds (input: $dtds){dtds {goid} detailedStatus {status description}}
        setJdbcConnections (input: $jdbcConnections){jdbcConnections {goid} detailedStatus {status description}}
        setFips (input: $fips){fips{goid} detailedStatus {status description}}
        setLdaps (input: $ldaps){ldaps{goid} detailedStatus {status description}}
        setFipGroups (input: $fipGroups){fipGroups{goid} detailedStatus {status description}}
        setInternalGroups (input: $internalGroups){internalGroups{goid} detailedStatus {status description}}
        setFipUsers (input: $fipUsers){fipUsers{goid} detailedStatus {status description}}
        setInternalUsers (input: $internalUsers){internalUsers{goid} detailedStatus {status description}}
        setCassandraConnections (input: $cassandraConnections){cassandraConnections {goid} detailedStatus {status description}}
        setJmsDestinations (input: $jmsDestinations){jmsDestinations {goid} detailedStatus {status description}}
        setSMConfigs (input: $smConfigs){smConfigs {goid} detailedStatus {status description}}
        setPolicyFragments (input: $policyFragments){policyFragments {goid} detailedStatus {status description}}
        setEncassConfigs (input: $encassConfigs){encassConfigs {goid} detailedStatus {status description}}
        setGlobalPolicies (input: $globalPolicies){globalPolicies {goid} detailedStatus {status description}}
        setBackgroundTaskPolicies (input: $backgroundTaskPolicies){backgroundTaskPolicies {goid} detailedStatus {status description}}
        setWebApiServices (input: $webApiServices){webApiServices {goid} detailedStatus {status description}}
        setSoapServices (input: $soapServices){soapServices {goid} detailedStatus {status description}}
        setInternalWebApiServices (input: $internalWebApiServices){internalWebApiServices {goid} detailedStatus {status description}}
        setInternalSoapServices (input: $internalSoapServices){internalSoapServices {goid} detailedStatus {status description}}
        setEmailListeners (input: $emailListeners){emailListeners {goid} detailedStatus {status description}}
        setListenPorts (input: $listenPorts){listenPorts {goid} detailedStatus {status description}}
        setActiveConnectors (input: $activeConnectors){activeConnectors {goid} detailedStatus {status description}}
        setScheduledTasks (input: $scheduledTasks){scheduledTasks {goid} detailedStatus {status description}}

        # Keys must be mutated at the end
        setKeys (input: $keys) {keys {alias} detailedStatus {status description}}
}

query policyFragmentByName ($name: String!) {
    policyFragmentByName (name: $name) {
        name
        folderPath
        policy {
            xml
            allDependencies {
              policyFragments {
                    name
                    checksum
                    folderPath
                    guid
                    policy {
                        xml
                    }
                }
                encassConfigs {
                    name
                    checksum
                    policyName
                    guid
                    encassArgs {
                        name
                        type
                        ordinal
                        guiLabel
                        guiPrompt
                    }
                    encassResults {
                        name
                        type
                    }
                }
                trustedCerts {
                    name
                    checksum
                    certBase64
                    verifyHostname
                    trustAnchor
                    trustedFor
                    revocationCheckPolicyType
                }
                clusterProperties {
                    name
                    checksum
                    value
                }
                jdbcConnections {
                    name
                    checksum
                    driverClass
                    jdbcUrl
                    enabled
                    username
                    password
                    minPoolSize
                    maxPoolSize
                    properties {
                        name
                        value
                    }
                }
                schemas {
                    systemId
                    checksum
                    targetNs
                    description
                    content
                }
                dtds {
                    systemId
                    checksum
                    publicId
                    description
                    content
                }
                fips {
                    name
                    checksum
                    goid
                    enableCredentialTypeX509
                    enableCredentialTypeSaml
                    certificateReferences {
                        thumbprintSha1
                    }
                }
                ldaps {
                    name
                    goid
                    ldapUrls
                    ldapsClientAuthEnabled
                    ldapsClientKeyAlias
                    searchBase
                    writable
                    bindDn
                    bindPassword
                    userMappings {
                        objClass
                        nameAttrName
                        loginAttrName
                        passwdAttrName
                        firstNameAttrName
                        lastNameAttrName
                        emailNameAttrName
                        kerberosAttrName
                        kerberosEnterpriseAttrName
                        userCertAttrName
                        passwdType {
                            val
                        }
                    }
                    groupMappings {
                        objClass
                        nameAttrName
                        memberAttrName
                        memberStrategy {
                            val
                        }
                    }
                    checksum
                }
                internalGroups {
                    name
                    checksum
                    goid
                    description
                }
                fipGroups {
                    name
                    providerName
                    checksum
                    goid
                    description
                }
                internalUsers {
                    name
                    goid
                    checksum
                    memberOf {
                        name
                    }
                    login
                    password
                    firstName
                    lastName
                    email
                    certBase64
                }
                fipUsers {
                    name
                    providerName
                    checksum
                    goid
                    subjectDn
                    memberOf {
                        name
                    }
                    login
                    firstName
                    lastName
                    email
                    certBase64
                }
                keys {
                    alias
                    checksum
                    goid
                    p12
                }
                secrets {
                    name
                    secretType
                    checksum
                    goid
                    variableReferencable
                    secret
                    description
                }
                cassandraConnections {
                    goid
                    name
                    keyspace
                    contactPoints
                    port
                    username
                    securePasswordName
                    sslEnabled
                    cipherSuites
                    properties {
                        name
                        value
                    }
                    checksum
                }
                jmsDestinations {
                    goid
                    connectionGoid
                    name
                    direction
                    providerType
                    initialContextFactoryClassname
                    connectionFactoryName
                    jndiUrl
                    jndiUsername
                    jndiPassword
                    template
                    enabled
                    jndiSslDetails {
                        sslEnabled
                        sslForAuthenticationOnly
                        sslVerifyServerCertificate
                        sslVerifyServerHostname
                        sslClientKeyAlias
                    }
                    destinationType
                    destinationName
                    destinationUsername
                    destinationPassword
                    destinationSslDetails {
                        sslEnabled
                        sslForAuthenticationOnly
                        sslVerifyServerCertificate
                        sslVerifyServerHostname
                        sslClientKeyAlias
                    }
                    template
                    enabled
                    properties {
                        name
                        value
                    }
                    checksum
                }
            }
        }
        guid
    }
}

